import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useParams, useHistory } from 'react-router-dom'; 


function UserDetails() {
  const [users, setUsers] = useState([]);
  const [filterValue, setFilterValue] = useState('');
  const { userId } = useParams();
  const history = useHistory(); // Initialize useHistory

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get('http://localhost:3005/users');
        setUsers(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchUsers();
  }, [userId]);

  const filterUsers = () => {
    // Filter users based on filterValue
    const filteredUsers = users.filter(user => {
      // Check if filterValue matches either unique number or full name
      return (
        user.uniqueNumber.toLowerCase().includes(filterValue.toLowerCase()) ||
        user.fullName.toLowerCase().includes(filterValue.toLowerCase())
      );
    });
    setUsers(filteredUsers);
  };

  // Function to handle clicking Data Entry button
  const handleDataEntry = (fullName, amount) => {
    // Navigate to UserData component with necessary data
    history.push({
      pathname: '/UserData',
      state: { fullName, amount }
    });
  };

  return (
    <div className='Main-table'>
      ...
      <tbody>
        {users.map(user => (
          <tr key={user._id}>
            ...
            <td className='td-Link'>
              <Link to={`/user/${user._id}`}>
                <span>View details</span>
              </Link>
              <button onClick={() => handleDataEntry(user.fullName, user.amount)}>Data Entry</button> {/* Change this */}
            </td>
          </tr>
        ))}
      </tbody>
    </div>
  );
}

export default UserDetails;
