import React from 'react';
import axios from 'axios';
import  { useState, useEffect }  from 'react';
import {useParams } from 'react-router-dom';

function UserDetailPage() {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(`http://localhost:3005/users/${userId}`);
        console.log('User ID:', userId);
        console.log('API Response:', res.data);
        setUser(res.data);
      } catch (err) {
        setError(err.message || 'Something went wrong!');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [userId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>User Details</h1>
      <p>FullName: {user.fullName}</p> <input type='text'></input>
      <p>Date of Birth: {user.dateOfBirth}</p>
      
      {/* {user.fullName && } */}
      {/* {user.dateOfBirth && } */}
      {user.address && <p>Address: {user.address}</p>}
      
      {user.phoneNumber && <p>Phone Number: {user.phoneNumber}</p>}
      {user.emailAddress && <p>Email Address: {user.emailAddress}</p>}
      {user.employmentDetails && <p>Employment Details: {user.employmentDetails}</p>}
      {user.bankingInformation && <p>Banking Information: {user.bankingInformation}</p>}
      {user.creditHistory && <p>Credit History: {user.creditHistory}</p>}
      {user.assetsAndLiabilities && <p>Assets and Liabilities: {user.assetsAndLiabilities}</p>}
      {user.maritalStatus && <p>Marital Status: {user.maritalStatus}</p>}
      {user.uniqueNumber && <p>Unique Number: {user.uniqueNumber}</p>}
    </div>
  );
}

export default UserDetailPage;
